import java.util.*

fun dp(remaining: Int, peopleLeft: Int, minPie: Int, memo: Array<Array<IntArray>>): Int {
    if (remaining < 0) {
        return 0
    }
    if (peopleLeft == 0) {
        return if (remaining == 0) 1 else 0
    }
    if (peopleLeft == 1) {
        return if (remaining >= minPie) 1 else 0
    }
    if (memo[remaining][peopleLeft][minPie] != -1) {
        return memo[remaining][peopleLeft][minPie]
    }

    val maxX = remaining / peopleLeft
    var total = 0
    for (x in minPie..maxX) {
        total += dp(remaining - x, peopleLeft - 1, x, memo)
    }

    memo[remaining][peopleLeft][minPie] = total
    return total
}

fun testPieDistribution() {
    val testCases = listOf(
        Triple(8, 4, 5),  // n=8, k=4, expected=5
        Triple(5, 2, 2),   // n=5, k=2, expected=2
        Triple(10, 3, 8)   // n=10, k=3, expected=8
    )

    for ((n, k, expected) in testCases) {
        val memo = Array(n + 2) { Array(k + 2) { IntArray(n + 2) { -1 } } }
        val result = dp(n, k, 1, memo)
        assert(result == expected) { "Failed for n=$n, k=$k. Expected $expected but got $result" }
    }
}

