// Function to find the length of the longest substring without repeating characters
fun longestSubstring(s: String): Int {
    // HashMap to store the last index of each character
    val charIndexMap = HashMap<Char, Int>()
    var longest = 0
    var start = 0

    for (i in s.indices) {
        val char = s[i]
        // If the character is already in the map and its index is >= start, update start
        if (charIndexMap.containsKey(char) && charIndexMap[char]!! >= start) {
            start = charIndexMap[char]!! + 1
        }
        // Update the last index of the character
        charIndexMap[char] = i
        // Update the longest substring length if necessary
        longest = maxOf(longest, i - start + 1)
    }

    return longest
}

