// Function to compute the number of folds needed to reduce dimension 'a' to fit 'b'
// The function counts how many times 'a' needs to be halved (rounded up) until it is <= 'b'
fun computeFolds(a: Int, b: Int): Int {
    var currentA = a
    var folds = 0
    while (currentA > b) {
        currentA = (currentA + 1) / 2
        folds++
    }
    return folds
}

// Function to determine the minimum number of folds required to fit a rectangle (w, h) into another rectangle (W, H)
// It checks both possible orientations of the target rectangle and returns the minimum folds required or -1 if not possible
fun solveMinFolds(W: Int, H: Int, w: Int, h: Int): Int {
    // Check if the rectangle fits in either orientation
    val fitsOrientation1 = w <= W && h <= H
    val fitsOrientation2 = h <= W && w <= H

    if (!fitsOrientation1 && !fitsOrientation2) {
        return -1
    }

    var res1 = Int.MAX_VALUE
    if (fitsOrientation1) {
        val foldsW = computeFolds(W, w)
        val foldsH = computeFolds(H, h)
        res1 = foldsW + foldsH
    }

    var res2 = Int.MAX_VALUE
    if (fitsOrientation2) {
        val foldsW = computeFolds(W, h)
        val foldsH = computeFolds(H, w)
        res2 = foldsW + foldsH
    }

    return minOf(res1, res2)
}

assertions:
fun testSolveMinFolds() {
    // Test case 1: Fits in orientation 1
    assert(solveMinFolds(10, 10, 5, 5) == 2)

    // Test case 2: Fits in orientation 2
    assert(solveMinFolds(10, 20, 15, 5) == 2)

    // Test case 3: Does not fit in any orientation
    assert(solveMinFolds(5, 5, 10, 10) == -1)

    // Test case 4: Fits in both orientations, but one requires fewer folds
    assert(solveMinFolds(16, 16, 4, 8) == 3)
}

testSolveMinFolds()

