// Function to find all unique pairs in the list that add up to the target sum.
// Each pair is sorted in non-descending order and the list of pairs is sorted
// in non-descending order as well.
fun findPairs(arr: IntArray, targetSum: Int): List<List<Int>> {
    // Sort the array to use two pointers approach
    val sortedArr = arr.sorted().toIntArray()
    var left = 0
    var right = sortedArr.size - 1
    val result = mutableSetOf<Pair<Int, Int>>() // To ensure uniqueness of pairs

    while (left < right) {
        val currentSum = sortedArr[left] + sortedArr[right]
        when {
            currentSum == targetSum -> {
                result.add(Pair(sortedArr[left], sortedArr[right]))
                left++
                right--
            }
            currentSum < targetSum -> left++
            else -> right--
        }
    }

    // Convert the set of pairs to a sorted list of lists
    return result.map { listOf(it.first, it.second) }.sortedWith(compareBy({ it[0] }, { it[1] }))
}

assertions:
