    assert(findPairs(intArrayOf(1, 2, 3, 4, 5), 5) == listOf(listOf(1, 4), listOf(2, 3)))

    // Test case 2: Test with duplicate numbers and multiple pairs
    assert(findPairs(intArrayOf(2, 2, 3, 4, 4), 6) == listOf(listOf(2, 4), listOf(3, 3)))

