import java.util.*

// Function to check if a directed graph has a cycle using Depth-First Search (DFS)
// The graph is represented as a map where each key is a node and the value is a list of adjacent nodes
fun hasCycle(graph: Map<Int, List<Int>>): Boolean {
    val visited = mutableSetOf<Int>()
    val recursionStack = mutableSetOf<Int>()

    // Nested DFS function to traverse the graph and detect cycles
    fun dfs(node: Int): Boolean {
        if (node !in visited) {
            visited.add(node)
            recursionStack.add(node)

            // Check all neighbors of the current node
            for (neighbor in graph.getOrDefault(node, emptyList())) {
                if (neighbor !in visited && dfs(neighbor)) {
                    return true
                } else if (neighbor in recursionStack) {
                    return true
                }
            }

            recursionStack.remove(node)
        }
        return false
    }

    // Iterate over all nodes in the graph to ensure all components are checked
    for (node in graph.keys) {
        if (node !in visited && dfs(node)) {
            return true
        }
    }
    return false
}

