// Function to generate a Fibonacci-like sequence where f(1) = 1 and f(2) = 2
fun generateFibonacciSequence(n: Int): IntArray {
    if (n <= 0) return intArrayOf()
    val f = IntArray(n + 2)
    f[1] = 1
    f[2] = 2
    for (i in 3..n) {
        f[i] = f[i - 1] + f[i - 2]
    }
    return f
}

// Function to generate the result sequence based on the given n and k
fun generateResultSequence(n: Int, k: Int): List<Int> {
    val f = generateFibonacciSequence(n)
    val res = mutableListOf<Int>()
    var i = 1
    var currentK = k

    while (i <= n) {
        if (i == n) {
            res.add(i)
            break
        }
        if (currentK <= f[n - i]) {
            res.add(i)
            i += 1
        } else {
            currentK -= f[n - i]
            res.add(i + 1)
            res.add(i)
            i += 2
        }
    }
    return res
}

