// Function to apply movements to initial positions and return sorted final positions
fun restoreSeasonTowers(initialPositions: MutableList<Pair<Int, Int>>, movements: List<Pair<Int, Int>>): List<Pair<Int, Int>> {
    // Define the movement directions
    val directions = mapOf(
        0 to Pair(0, 1),   // North
        1 to Pair(1, 0),   // East
        2 to Pair(0, -1),  // South
        3 to Pair(-1, 0)   // West
    )

    // Apply all movements to each crystal
    for (movement in movements) {
        val (direction, distance) = movement
        val (dx, dy) = directions[direction] ?: Pair(0, 0)
        // Update each crystal's position
        for (i in initialPositions.indices) {
            val (x, y) = initialPositions[i]
            initialPositions[i] = Pair(x + dx * distance, y + dy * distance)
        }
    }

    // Sort the final positions primarily by x-coordinate, secondarily by y-coordinate
    return initialPositions.sortedWith(compareBy({ it.first }, { it.second }))
}

