import kotlin.math.ceil
import kotlin.math.sqrt

/**
 * Encrypts a string using a square grid rotation method.
 * The steps are:
 * 1. Pad the string to make its length a perfect square.
 * 2. Arrange the string into a square grid.
 * 3. Rotate the grid 90 degrees clockwise.
 * 4. Read the grid row-wise, skipping padding characters.
 *
 * @param s The input string to be encrypted.
 * @return The encrypted string.
 */
fun encryptString(s: String): String {
    val l = s.length
    val k = ceil(sqrt(l.toDouble())).toInt()
    val m = k * k
    val pad = s + "*".repeat(m - l)

    // Create original grid
    val originalGrid = List(k) { i ->
        pad.substring(i * k, (i + 1) * k)
    }

    // Reverse the grid (rotate 180 degrees)
    val reversedGrid = originalGrid.reversed()

    // Rotate 90 degrees clockwise (transpose and reverse columns)
    val rotatedGrid = List(k) { i ->
        List(k) { j ->
            reversedGrid[j][i]
        }
    }

    // Collect non-padding characters
    return rotatedGrid.flatMap { row ->
        row.filter { it != '*' }
    }.joinToString("")
}

