/**
 * This Kotlin script compares the performance of two characters, Megumin and Yunyun,
 * based on their given parameters (tm, sm, ty, sy) and determines the better performer.
 * The comparison is done using cross multiplication of their calculated scores.
 */
fun comparePerformance(tm: Int, sm: Int, ty: Int, sy: Int): String {
    // Calculate Megumin's score
    val (aMeg, bMeg) = if (tm >= 9 * sm) {
        Pair(100, tm + sm)
    } else {
        Pair(90, tm)
    }

    // Calculate Yunyun's score
    val (aYun, bYun) = if (ty >= 9 * sy) {
        Pair(100, ty + sy)
    } else {
        Pair(90, ty)
    }

    // Compare using cross multiplication
    val megTime = aMeg * bYun
    val yunTime = aYun * bMeg

    return when {
        megTime < yunTime -> "Megumin"
        megTime > yunTime -> "Yunyun"
        else -> "Draw"
    }
}

