/**
 * Determines the maximum number of pictures Lisa can hang without exceeding the weight limit.
 * 
 * @param n The number of pictures.
 * @param weights List of weights of the pictures.
 * @param W The maximum weight the wall can hold.
 * @return The maximum number of pictures that can be hung without exceeding the weight limit.
 */
fun maxPictures(n: Int, weights: List<Int>, W: Int): Int {
    val sortedWeights = weights.sorted()
    var totalWeight = 0
    var count = 0

    for (weight in sortedWeights) {
        if (totalWeight + weight <= W) {
            totalWeight += weight
            count++
        } else {
            break
        }
    }

    return count
}

