import java.util.*
import kotlin.collections.HashSet

/**
 * Checks if a given string of length K is a palindrome.
 * A string is a palindrome if it reads the same forwards and backwards.
 *
 * @param str The string to check.
 * @param K The length of the substring to check.
 * @return true if the string is a palindrome, false otherwise.
 */
fun isPalindrome(str: String, K: Int): Boolean {
    for (i in 0 until K / 2) {
        if (str[i] != str[K - 1 - i]) {
            return false
        }
    }
    return true
}

/**
 * Generates all unique permutations of a given string.
 *
 * @param str The input string.
 * @return A set of all unique permutations of the input string.
 */
fun generateUniquePermutations(str: String): Set<String> {
    val uniquePerms = HashSet<String>()
    val chars = str.toCharArray()
    permute(chars, 0, uniquePerms)
    return uniquePerms
}

/**
 * Helper function to generate permutations recursively.
 *
 * @param chars The character array to permute.
 * @param index The current index in the permutation process.
 * @param uniquePerms The set to store unique permutations.
 */
private fun permute(chars: CharArray, index: Int, uniquePerms: HashSet<String>) {
    if (index == chars.size - 1) {
        uniquePerms.add(String(chars))
        return
    }
    for (i in index until chars.size) {
        swap(chars, index, i)
        permute(chars, index + 1, uniquePerms)
        swap(chars, index, i)
    }
}

/**
 * Swaps two elements in a character array.
 *
 * @param chars The character array.
 * @param i The first index.
 * @param j The second index.
 */
private fun swap(chars: CharArray, i: Int, j: Int) {
    val temp = chars[i]
    chars[i] = chars[j]
    chars[j] = temp
}

/**
 * Counts the number of unique permutations of a string where no K-length substring is a palindrome.
 *
 * @param N The length of the string.
 * @param K The length of the substring to check for palindromes.
 * @param S The input string.
 * @return The count of valid permutations.
 */
fun countValidPermutations(N: Int, K: Int, S: String): Int {
    val uniquePerms = generateUniquePermutations(S)
    var count = 0

    for (perm in uniquePerms) {
        var hasPalindrome = false
        for (i in 0..N - K) {
            val substring = perm.substring(i, i + K)
            if (isPalindrome(substring, K)) {
                hasPalindrome = true
                break
            }
        }
        if (!hasPalindrome) {
            count++
        }
    }

    return count
}

