// Function to calculate the maximum sum of elements in any row or column of a square matrix
fun maxSumInMatrix(matrix: Array<IntArray>): Int {
    val n = matrix.size
    var maxSum = Int.MIN_VALUE

    // Check row sums
    for (row in matrix) {
        val rowSum = row.sum()
        if (rowSum > maxSum) {
            maxSum = rowSum
        }
    }

    // Check column sums
    for (col in 0 until n) {
        var colSum = 0
        for (row in 0 until n) {
            colSum += matrix[row][col]
        }
        if (colSum > maxSum) {
            maxSum = colSum
        }
    }

    return maxSum
}

