// Function to determine the musical key (A minor or C major) based on the given measures.
// The decision is made by counting the occurrences of specific starting notes in each measure.
// If counts are equal, the last note of the entire string is used to decide.
fun determineMusicalKey(measuresString: String): String {
    val measures = measuresString.split('|')
    var aMinorCount = 0
    var cMajorCount = 0

    val aMinorTones = setOf('A', 'D', 'E')
    val cMajorTones = setOf('C', 'F', 'G')

    for (measure in measures) {
        if (measure.isNotEmpty()) {
            val firstNote = measure[0]
            when (firstNote) {
                in aMinorTones -> aMinorCount++
                in cMajorTones -> cMajorCount++
            }
        }
    }

    return when {
        aMinorCount > cMajorCount -> "A-mol"
        cMajorCount > aMinorCount -> "C-dur"
        else -> {
            val lastNote = if (measuresString.isNotEmpty()) measuresString.last() else ' '
            if (lastNote == 'A') "A-mol" else "C-dur"
        }
    }
}

