    for (measure in measures) {
        if (measure.isNotEmpty()) {
            val firstNote = measure[0]
            when (firstNote) {
                in aMinorTones -> aMinorCount++
                in cMajorTones -> cMajorCount++
            }
        }
    }

    return when {
        aMinorCount > cMajorCount -> "A-mol"
        cMajorCount > aMinorCount -> "C-dur"
        else -> {
            val lastNote = if (measuresString.isNotEmpty()) measuresString.last() else ' '
            if (lastNote == 'A') "A-mol" else "C-dur"
        }
    }
}

fun testDetermineMusicalKey() {
    assert(determineMusicalKey("A|D|E|F") == "A-mol")
    assert(determineMusicalKey("C|F|G|A") == "C-dur")
    assert(determineMusicalKey("A|C|D|G") == "A-mol")
    assert(determineMusicalKey("A|D|E|A") == "A-mol")
    assert(determineMusicalKey("C|F|G|C") == "C-dur")
    assert(determineMusicalKey("|A|D|E|") == "A-mol")
    assert(determineMusicalKey("|C|F|G|") == "C-dur")
    assert(determineMusicalKey("") == "C-dur")
}

