// This Kotlin code simulates the process of merging adjacent blocks with the same value to create a new block with value v+1.
// The goal is to find the maximum possible value that can be achieved through such merges.

fun findMaxPossibleValue(a: IntArray): Int {
    val n = a.size
    val maxPossibleV = 60 // A sufficiently large upper limit to cover all possible cases

    // Initialize dp array, dp[v][i] represents the farthest end position starting from i to synthesize v
    val dp = Array(maxPossibleV + 1) { IntArray(n) { -1 } }
    var maxVal = a.maxOrNull() ?: 0

    // Initialize each position's initial value
    for (i in a.indices) {
        val vInitial = a[i]
        if (vInitial > maxPossibleV) {
            continue // Skip if initial value exceeds maxPossibleV
        }
        dp[vInitial][i] = i // Initialize the block for the initial value
    }

    // Process each possible v value
    for (v in 1..maxPossibleV) {
        for (i in 0 until n) {
            if (dp[v - 1][i] == -1) {
                continue // Cannot be a starting point
            }
            val k = dp[v - 1][i] // End position of the v-1 block
            val j = k + 1
            if (j >= n) {
                continue
            }
            if (dp[v - 1][j] == -1) {
                continue // Next block does not exist
            }
            val end = dp[v - 1][j] // End position after merging
            if (dp[v][i] < end) {
                dp[v][i] = end
                if (v > maxVal) {
                    maxVal = v
                }
            }
        }
    }

    return maxVal
}

