import java.util.*

// oddOccurrences returns a list containing only the elements that appear an odd number of times in the input array.
// The returned list is sorted in ascending order.
fun oddOccurrences(arr: Array<Int>): List<Int> {
    val counts = HashMap<Int, Int>()

    // Count occurrences of each element in the array
    for (elem in arr) {
        counts[elem] = counts.getOrDefault(elem, 0) + 1
    }

    // Filter elements with odd counts
    val oddElements = counts.filter { (_, count) -> count % 2 != 0 }.keys.toList()

    // Return sorted list
    return oddElements.sorted()
}

