import java.util.*

/**
 * Finds the best position in a grid based on a scoring system that considers neighboring cells.
 * The score for each cell is calculated by:
 * - Adding the absolute value of a neighbor if the current cell and neighbor have opposite signs
 * - Subtracting the absolute value of a neighbor if they have the same sign
 * The best position is the one with the highest score, with ties broken by smaller row index then column index.
 */
fun findBestPosition(grid: Array<IntArray>): Triple<Int, Int, Int> {
    val directions = arrayOf(-1 to 0, 1 to 0, 0 to -1, 0 to 1)
    var maxScore = Int.MIN_VALUE
    var bestI = -1
    var bestJ = -1

    for (i in grid.indices) {
        for (j in grid[i].indices) {
            val current = grid[i][j]
            var score = 0

            for ((dx, dy) in directions) {
                val ni = i + dx
                val nj = j + dy

                if (ni in grid.indices && nj in grid[ni].indices) {
                    val neighbor = grid[ni][nj]
                    score += if (current * neighbor < 0) abs(neighbor) else -abs(neighbor)
                }
            }

            when {
                score > maxScore -> {
                    maxScore = score
                    bestI = i
                    bestJ = j
                }
                score == maxScore -> {
                    if (i < bestI || (i == bestI && j < bestJ)) {
                        bestI = i
                        bestJ = j
                    }
                }
            }
        }
    }

    return Triple(bestI + 1, bestJ + 1, maxScore)
}

/**
 * Helper function to get absolute value of an Int
 */
fun abs(x: Int) = if (x < 0) -x else x

assertions:
