    assert(result1 == Triple(2, 2, 5))

    // Test case 2: All positive numbers
    val grid2 = arrayOf(
        intArrayOf(1, 2),
        intArrayOf(3, 4)
    )
    val result2 = findBestPosition(grid2)
    assert(result2 == Triple(1, 1, -5))

    // Test case 3: Larger grid with zeros
    val grid3 = arrayOf(
        intArrayOf(0, -5, 10),
        intArrayOf(3, 0, -2),
        intArrayOf(-1, 4, 0)
    )
    val result3 = findBestPosition(grid3)
    assert(result3 == Triple(1, 2, 7))

    // Test case 4: Single cell grid
    val grid4 = arrayOf(
        intArrayOf(42)
    )
    val result4 = findBestPosition(grid4)
    assert(result4 == Triple(1, 1, 0))
}

