// This Kotlin program computes the XOR of a range of elements in an array after doubling each element.
// The solution uses a prefix XOR array to efficiently answer multiple range queries.

// Data class to hold test case information
data class TestCase(
    val n: Int,
    val m: Int,
    val a: List<Int>,
    val queries: List<Pair<Int, Int>>
)

// Computes prefix XOR array where each element is doubled before XOR operation
fun computePrefixXor(a: List<Int>): IntArray {
    val prefixXor = IntArray(a.size + 1)
    for (i in 1..a.size) {
        prefixXor[i] = prefixXor[i - 1] xor (2 * a[i - 1])
    }
    return prefixXor
}

// Processes multiple range XOR queries using the prefix XOR array
fun processQueries(n: Int, m: Int, a: List<Int>, queries: List<Pair<Int, Int>>): List<Int> {
    val prefixXor = computePrefixXor(a)
    val results = mutableListOf<Int>()
    for (query in queries) {
        val (l, r) = query
        results.add(prefixXor[r] xor prefixXor[l - 1])
    }
    return results
}

