/**
 * Converts a list of dictionaries into a single string where each key-value pair is separated by a colon,
 * and each person's record is separated by a semicolon and a space.
 *
 * @param listOfDicts List of maps with keys "name", "age", and "city"
 * @return A single concatenated string
 */
fun convertListOfDictsToString(listOfDicts: List<Map<String, Any>>): String {
    return listOfDicts.joinToString("; ") { person ->
        "name:${person["name"]}; age:${person["age"]}; city:${person["city"]}"
    }
}

// Function to calculate the optimal coordinate based on the sum of coordinates and count
fun calculateOptimalCoordinate(sumCoord: Int, count: Int): Int {
    return if ((2 * sumCoord) % count == 0) {
        val m = (2 * sumCoord) / count
        if (m % 2 == 1) (m - 1) / 2 else m / 2
    } else {
        val avg = sumCoord.toDouble() / count
        (avg + 0.5 - 1e-9).toInt()
    }
}

// Function to process a test case with given points and return the optimal (x, y) coordinate
fun processTestCase(points: List<Pair<Int, Int>>): Pair<Int, Int> {
    val sumX = points.sumOf { it.first }
    val sumY = points.sumOf { it.second }
    val count = points.size

    val xAns = calculateOptimalCoordinate(sumX, count)
    val yAns = calculateOptimalCoordinate(sumY, count)

    return Pair(xAns, yAns)
}

