import kotlin.math.ceil
import kotlin.math.max

/**
 * Solves the problem of finding the minimum maximum segment size when dividing K segments among N parts.
 * The function uses binary search to find the smallest maximum segment size such that the total number of segments
 * required (sum of ceil(P[i] / segmentSize)) is less than or equal to K.
 *
 * @param K The total number of segments available.
 * @param N The number of parts to divide.
 * @param P The list of part sizes.
 * @return The minimum maximum segment size as a Double, or "Impossible" if K < N.
 */
fun solveSegmentDivision(K: Int, N: Int, P: List<Int>): Any {
    if (K < N) {
        return "Impossible"
    }
    val maxP = P.maxOrNull() ?: 0
    var low = 0.0
    var high = maxP.toDouble()

    repeat(100) { // Sufficient iterations for precision
        val mid = (low + high) / 2
        val required = if (mid == 0.0) {
            Double.POSITIVE_INFINITY
        } else {
            P.sumOf { p -> ceil(p / mid) }
        }
        if (required <= K) {
            high = mid
        } else {
            low = mid
        }
    }
    return high
}

assertions:
fun testSolveSegmentDivision() {
    // Test case 1: K >= N
    val result1 = solveSegmentDivision(5, 3, listOf(4, 5, 6))
    assert(result1 is Double && result1 == 3.0)

    // Test case 2: K < N (Impossible)
    val result2 = solveSegmentDivision(2, 3, listOf(1, 2, 3))
    assert(result2 == "Impossible")

    // Test case 3: K >= N
    val result3 = solveSegmentDivision(10, 4, listOf(8, 7, 6, 5))
    assert(result3 is Double && result3 == 4.0)

    // Test case 4: Termination case (not processed)
    val result4 = solveSegmentDivision(0, 0, emptyList<Int>())
    assert(result4 == "Impossible")
}

testSolveSegmentDivision()

