// Node class represents a node in a Binary Search Tree (BST)
class Node(var key: Int) {
    var left: Node? = null
    var right: Node? = null
}

// BST class represents a Binary Search Tree with various operations
class BST {
    var root: Node? = null

    // insert inserts a new key into the BST
    fun insert(key: Int) {
        if (root == null) {
            root = Node(key)
        } else {
            insertRecursive(root, key)
        }
    }

    // insertRecursive is a helper function to insert a key recursively
    private fun insertRecursive(node: Node?, key: Int): Node {
        if (node == null) {
            return Node(key)
        }
        if (key < node.key) {
            node.left = insertRecursive(node.left, key)
        } else {
            node.right = insertRecursive(node.right, key)
        }
        return node
    }

    // height calculates the height of the BST
    fun height(): Int {
        return heightRecursive(root)
    }

    // heightRecursive is a helper function to calculate the height recursively
    private fun heightRecursive(node: Node?): Int {
        if (node == null) {
            return -1
        }
        val leftHeight = heightRecursive(node.left)
        val rightHeight = heightRecursive(node.right)
        return maxOf(leftHeight, rightHeight) + 1
    }

    // countLeaves counts the number of leaf nodes in the BST
    fun countLeaves(): Int {
        return countLeavesRecursive(root)
    }

    // countLeavesRecursive is a helper function to count leaves recursively
    private fun countLeavesRecursive(node: Node?): Int {
        if (node == null) {
            return 0
        }
        if (node.left == null && node.right == null) {
            return 1
        }
        return countLeavesRecursive(node.left) + countLeavesRecursive(node.right)
    }

    // countInternalNodes counts the number of internal nodes in the BST
    fun countInternalNodes(): Int {
        return countInternalNodesRecursive(root)
    }

    // countInternalNodesRecursive is a helper function to count internal nodes recursively
    private fun countInternalNodesRecursive(node: Node?): Int {
        if (node == null || (node.left == null && node.right == null)) {
            return 0
        }
        return 1 + countInternalNodesRecursive(node.left) + countInternalNodesRecursive(node.right)
    }

    // isBalanced checks if the BST is balanced
    fun isBalanced(): Boolean {
        return isBalancedRecursive(root)
    }

    // isBalancedRecursive is a helper function to check balance recursively
    private fun isBalancedRecursive(node: Node?): Boolean {
        if (node == null) {
            return true
        }
        val leftHeight = heightRecursive(node.left)
        val rightHeight = heightRecursive(node.right)
        if (Math.abs(leftHeight - rightHeight) > 1) {
            return false
        }
        return isBalancedRecursive(node.left) && isBalancedRecursive(node.right)
    }
}

