// Function to find the number of contiguous subarrays that sum up to a target value k.
// The function uses a hash map to store cumulative sums and their frequencies for efficient computation.
fun findSubarraysWithSum(n: Int, k: Int, arr: Array<Int>): Int {
    var count = 0
    var cumSum = 0
    val sumFreq = mutableMapOf<Int, Int>()
    sumFreq[0] = 1 // Initialize with a cumulative sum of 0 having frequency 1

    for (num in arr) {
        cumSum += num

        // If (cumSum - k) exists in the map, add its frequency to the count
        count += sumFreq.getOrDefault(cumSum - k, 0)

        // Update the frequency of the current cumulative sum in the map
        sumFreq[cumSum] = sumFreq.getOrDefault(cumSum, 0) + 1
    }

    return count
}

