import java.util.Arrays

/**
 * Generates all unique permutations of the input list of integers in lexicographic order.
 *
 * @param nums A list of integers where each integer can appear multiple times.
 * @return A list of integer arrays, each containing a unique permutation of the input list.
 */
fun uniquePermutations(nums: List<Int>): List<IntArray> {
    // Generate all permutations using a recursive approach
    val permutations = mutableListOf<IntArray>()
    val used = BooleanArray(nums.size)
    val currentPermutation = IntArray(nums.size)
    val sortedNums = nums.sorted().toIntArray()

    generatePermutations(sortedNums, used, currentPermutation, 0, permutations)

    return permutations.distinctBy { it.contentToString() }
}

/**
 * Helper function to generate permutations recursively.
 *
 * @param nums The sorted input array of integers.
 * @param used Boolean array to track which elements have been used in the current permutation.
 * @param currentPermutation The current permutation being built.
 * @param index The current index in the permutation being filled.
 * @param permutations The list to store all generated permutations.
 */
private fun generatePermutations(
    nums: IntArray,
    used: BooleanArray,
    currentPermutation: IntArray,
    index: Int,
    permutations: MutableList<IntArray>
) {
    if (index == nums.size) {
        permutations.add(currentPermutation.copyOf())
        return
    }

    for (i in nums.indices) {
        if (!used[i]) {
            if (i > 0 && nums[i] == nums[i - 1] && !used[i - 1]) {
                continue // Skip duplicates to avoid redundant permutations
            }
            used[i] = true
            currentPermutation[index] = nums[i]
            generatePermutations(nums, used, currentPermutation, index + 1, permutations)
            used[i] = false
        }
    }
}

