// Function to find all unique triplets in an array that sum up to zero.
// The function uses a two-pointer approach after sorting the array to efficiently find the triplets.
fun uniqueTriplets(nums: IntArray): List<List<Int>> {
    nums.sort()
    val result = mutableListOf<List<Int>>()

    for (i in 0 until nums.size - 2) {
        // Skip duplicate elements for the first element of the triplet
        if (i > 0 && nums[i] == nums[i - 1]) {
            continue
        }

        var leftPos = i + 1
        var rightPos = nums.size - 1

        while (leftPos < rightPos) {
            val tripletSum = nums[i] + nums[leftPos] + nums[rightPos]

            when {
                tripletSum == 0 -> {
                    result.add(listOf(nums[i], nums[leftPos], nums[rightPos]))
                    // Skip duplicate elements for the second element of the triplet
                    while (leftPos < rightPos && nums[leftPos] == nums[leftPos + 1]) {
                        leftPos++
                    }
                    // Skip duplicate elements for the third element of the triplet
                    while (leftPos < rightPos && nums[rightPos] == nums[rightPos - 1]) {
                        rightPos--
                    }
                    leftPos++
                    rightPos--
                }
                tripletSum < 0 -> {
                    leftPos++
                }
                else -> {
                    rightPos--
                }
            }
        }
    }

    return result
}

