import kotlin.math.sqrt

// getDivisors calculates all the divisors of a given number k.
// It returns a set of integers representing all divisors of k.
fun getDivisors(k: Int): Set<Int> {
    val divisors = mutableSetOf<Int>()
    for (i in 1..sqrt(k.toDouble()).toInt()) {
        if (k % i == 0) {
            divisors.add(i)
            divisors.add(k / i)
        }
    }
    return divisors
}

// calculateTotalContributions computes the total contributions of valid pairs (a, b)
// where a is a divisor of k, and b = k / a, such that a <= maxA and b <= maxB.
// The contribution for each valid pair is (n - a) * (m - b).
// The result is returned modulo 10^9 + 7.
fun calculateTotalContributions(n: Int, m: Int, k: Int): Int {
    val divisors = getDivisors(k)
    val mod = 1_000_000_007
    var ans = 0
    val maxA = n - 1
    val maxB = m - 1

    for (a in divisors) {
        val b = k / a
        if (a <= maxA && b <= maxB) {
            val contribution = (n - a) * (m - b)
            ans = (ans + contribution) % mod
        }
    }
    return ans % mod
}

assertions:
fun testCalculateTotalContributions() {
    // Test case 1: n=3, m=4, k=2
    assert(calculateTotalContributions(3, 4, 2) == 14)

    // Test case 2: n=5, m=5, k=10
    assert(calculateTotalContributions(5, 5, 10) == 1)

    // Test case 3: n=1, m=1, k=1
    assert(calculateTotalContributions(1, 1, 1) == 1)

    // Test case 4: n=10, m=10, k=5
    assert(calculateTotalContributions(10, 10, 5) == 396)
}

testCalculateTotalContributions()

