 * Finds all special pairs (i, j) in the given array of numbers such that:
 * - 0 <= i, j < nums.size
 * - i < j
 * - nums[i] + nums[j] is an even number
 *
 * @param nums Array of integers to search for special pairs
 * @return List of pairs (i, j) that meet the criteria
 */
fun findSpecialPairs(nums: IntArray): List<Pair<Int, Int>> {
    val specialPairs = mutableListOf<Pair<Int, Int>>()
    val n = nums.size

    for (i in 0 until n) {
        for (j in i + 1 until n) {
            if ((nums[i] + nums[j]) % 2 == 0) {
                specialPairs.add(Pair(i, j))
            }
        }
    }

    return specialPairs
}

assertions:
fun testFindSpecialPairs() {
    // Test case 1: Mixed even and odd numbers
    val result1 = findSpecialPairs(intArrayOf(1, 2, 3, 4))
    assert(result1 == listOf(Pair(0, 2), Pair(1, 3)))

    // Test case 2: All even numbers
    val result2 = findSpecialPairs(intArrayOf(0, 2, 4, 6))
    assert(result2 == listOf(Pair(0, 1), Pair(0, 2), Pair(0, 3), Pair(1, 2), Pair(1, 3), Pair(2, 3)))

    // Test case 3: All odd numbers
    val result3 = findSpecialPairs(intArrayOf(1, 3, 5, 7))
    assert(result3 == listOf(Pair(0, 1), Pair(0, 2), Pair(0, 3), Pair(1, 2), Pair(1, 3), Pair(2, 3)))

    // Test case 4: Mixed with Fibonacci sequence
    val result4 = findSpecialPairs(intArrayOf(1, 2, 3, 5, 8))
    assert(result4 == listOf(Pair(0, 2), Pair(0, 3), Pair(1, 4)))
}

testFindSpecialPairs()

