// Function to check if a string is a palindrome
fun isPalindrome(s: String): Boolean {
    return s == s.reversed()
}

// Function to count the number of palindromic pairs in a list of words
// A palindromic pair is formed by concatenating two different words from the list,
// and the resulting string is a palindrome.
fun countPalindromicPairs(words: List<String>): Int {
    var count = 0
    for (i in words.indices) {
        for (j in i + 1 until words.size) {
            if (isPalindrome(words[i] + words[j])) {
                count++
            }
        }
    }
    return count
}

assertions:
fun testCountPalindromicPairs() {
    // Test case 1: ["bat", "tab", "cat"] should have 2 palindromic pairs
    // ("bat"+"tab" and "tab"+"bat")
    assert(countPalindromicPairs(listOf("bat", "tab", "cat")) == 2)

    // Test case 2: ["aaa", "aa", "a"] should have 6 palindromic pairs
    // (all combinations except same-index pairs)
    assert(countPalindromicPairs(listOf("aaa", "aa", "a")) == 6)

    // Test case 3: ["hello", "world"] should have 0 palindromic pairs
    assert(countPalindromicPairs(listOf("hello", "world")) == 0)

    // Test case 4: ["a", "b", "c", "d"] should have 0 palindromic pairs
    assert(countPalindromicPairs(listOf("a", "b", "c", "d")) == 0)
}

testCountPalindromicPairs()

