// Function to find the element that appears exactly three times in a list of integers
// where all other elements appear exactly twice.
fun findElementAppearsThreeTimes(nums: List<Int>): Int {
    val numCount = mutableMapOf<Int, Int>()

    // Count the occurrences of each number in the list
    for (num in nums) {
        numCount[num] = numCount.getOrDefault(num, 0) + 1
    }

    // Find and return the number that appears three times
    for ((num, count) in numCount) {
        if (count == 3) {
            return num
        }
    }

    // In case no such number is found (though problem states there is one)
    throw IllegalArgumentException("No element appears exactly three times in the list.")
}

assertions:
fun testFindElementAppearsThreeTimes() {
    assert(findElementAppearsThreeTimes(listOf(1, 1, 2, 2, 3, 3, 3)) == 3)
    assert(findElementAppearsThreeTimes(listOf(4, 4, 5, 5, 5, 6, 6)) == 5)
    assert(findElementAppearsThreeTimes(listOf(7, 8, 8, 9, 9, 10, 10, 10)) == 10)
}

testFindElementAppearsThreeTimes()

