// Function to determine if a graph can be converted into a tree by removing at most K edges.
// A tree must have exactly N-1 edges and no cycles.
fun canBeTree(N: Int, M: Int, K: Int, edges: List<Triple<Int, Int, Int>>): String {
    // A tree needs exactly N-1 edges to be connected and acyclic.
    val minRequiredEdges = N - 1

    // If there are less than N-1 edges, it can never be a tree.
    if (M < minRequiredEdges) {
        return "No"
    }

    // Check if we can remove enough edges to make the total edges exactly N-1
    if (M - K <= minRequiredEdges) {
        return "Yes"
    }

    return "No"
}

fun testCanBeTree() {
    // Test Case 1: Can be converted into a tree by removing at most K edges
    val N1 = 4
    val M1 = 5
    val K1 = 2
    val edges1 = listOf(
        Triple(1, 2, 1),
        Triple(2, 3, 2),
        Triple(3, 4, 3),
        Triple(1, 3, 4),
        Triple(2, 4, 5)
    )
    assert(canBeTree(N1, M1, K1, edges1) == "Yes")

    // Test Case 2: Cannot be converted into a tree because M < N-1
    val N2 = 3
    val M2 = 1
    val K2 = 1
    val edges2 = listOf(
        Triple(1, 2, 1)
    )
    assert(canBeTree(N2, M2, K2, edges2) == "No")

    // Test Case 3: Can be converted into a tree by removing exactly K edges
    val N3 = 5
    val M3 = 7
    val K3 = 3
    val edges3 = listOf(
        Triple(1, 2, 1),
        Triple(2, 3, 2),
        Triple(3, 4, 3),
        Triple(4, 5, 4),
        Triple(1, 3, 5),
        Triple(2, 4, 6),
        Triple(3, 5, 7)
    )
    assert(canBeTree(N3, M3, K3, edges3) == "Yes")

    // Test Case 4: Cannot be converted into a tree because M - K > N-1
    val N4 = 4
    val M4 = 6
    val K4 = 2
    val edges4 = listOf(
        Triple(1, 2, 1),
        Triple(2, 3, 2),
        Triple(3, 4, 3),
        Triple(1, 3, 4),
        Triple(2, 4, 5),
        Triple(1, 4, 6)
    )
    assert(canBeTree(N4, M4, K4, edges4) == "No")
}

testCanBeTree()
