function Test-MatrixConditions {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$n,

        [Parameter(Mandatory = $true)]
        [array]$matrices
    )

    for ($matrixIndex = 0; $matrixIndex -lt $n; $matrixIndex++) {
        $matrixData = $matrices[$matrixIndex]
        $r = $matrixData.Rows
        $c = $matrixData.Columns
        $matrix = $matrixData.Matrix

        $sumBlack = 0
        $sumWhite = 0
        $countOdds = 0

        for ($i = 0; $i -lt $r; $i++) {
            for ($j = 0; $j -lt $c; $j++) {
                $val = $matrix[$i][$j]
                if (($i + $j) % 2 -eq 0) {
                    $sumBlack += $val
                } else {
                    $sumWhite += $val
                }
                if ($val % 2 -ne 0) {
                    $countOdds++
                }
            }
        }

        if ($sumBlack -ne $sumWhite) {
            "NO"
        } else {
            if ($countOdds % 2 -eq 0) { "YES" } else { "NO" }
        }
    }
}

