function Calculate-MinimalDays {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$t,
        [Parameter(Mandatory = $true)]
        [array]$testCases
    )

    for ($i = 0; $i -lt $t; $i++) {
        $k = $testCases[$i * 2]
        $a = $testCases[$i * 2 + 1]

        $c = ($a | Measure-Object -Sum).Sum
        $fullWeeks = [Math]::Floor(($k - 1) / $c)
        $remainingDays = $k - $fullWeeks * $c

        $aExtended = $a + $a
        $minLen = [double]::PositiveInfinity
        $count = 0
        $left = 0

        for ($right = 0; $right -lt $aExtended.Count; $right++) {
            if ($aExtended[$right] -eq 1) {
                $count++
            }
            while ($count -eq $remainingDays) {
                $currentLen = $right - $left + 1
                if ($currentLen -lt $minLen) {
                    $minLen = $currentLen
                }
                if ($aExtended[$left] -eq 1) {
                    $count--
                }
                $left++
            }
        }
        $totalDays = $fullWeeks * 7 + $minLen
        Write-Output "Total days for k=$k and pattern=$($a -join ','): $totalDays"
    }
}

