    )

    $results = Calculate-MinimalDays -t $testCases[0] -testCases $testCases[1..($testCases.Count - 1)]

    $expectedResults = @(
        "Total days for k=2 and pattern=0,1,0,0,0,0,0: 8",
        "Total days for k=100000000 and pattern=1,0,0,0,1,0,1: 233333332",
        "Total days for k=1 and pattern=1,0,0,0,0,0,0: 1"
    )

    for ($i = 0; $i -lt $expectedResults.Count; $i++) {
        if ($results[$i] -ne $expectedResults[$i]) {
            throw "Test case $($i+1) failed. Expected: '$($expectedResults[$i])', Actual: '$($results[$i])'"
        }
    }

    Write-Host "All test cases passed"
}

