function Count-BuildingsWithScenicView {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    $count = 0
    for ($i = 0; $i -lt $Numbers.Count; $i++) {
        $hasScenicView = $true
        for ($j = $i + 1; $j -lt $Numbers.Count; $j++) {
            if ($Numbers[$j] -ge $Numbers[$i]) {
                $hasScenicView = $false
                break
            }
        }
        if ($hasScenicView) {
            $count++
        }
    }
    return $count
}

