# Identify-HeatwaveDays identifies the indices of days that are part of a heatwave.
# A heatwave is defined as a period of consecutive days where the temperature exceeds a given threshold.
# The function returns the indices of all days that are part of any such heatwave period.
function Identify-HeatwaveDays {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [double[]]$Temperatures,

        [Parameter(Mandatory = $true)]
        [double]$Threshold,

        [Parameter(Mandatory = $true)]
        [int]$ConsecutiveDays
    )

    $heatwaveIndices = @()
    $count = 0

    for ($i = 0; $i -lt $Temperatures.Count; $i++) {
        if ($Temperatures[$i] -gt $Threshold) {
            $count++
            if ($count -ge $ConsecutiveDays) {
                for ($j = $i - $ConsecutiveDays + 1; $j -le $i; $j++) {
                    if ($j -notin $heatwaveIndices) {
                        $heatwaveIndices += $j
                    }
                }
            }
        } else {
            $count = 0
        }
    }

    return $heatwaveIndices
}

