function Compare-Arrays {
    param($a, $b)

    if ($a.Count -ne $b.Count) {
        return $false
    }

    for ($i = 0; $i -lt $a.Count; $i++) {
        if ($a[$i] -ne $b[$i]) {
            return $false
        }
    }

    return $true
}

function Test-IdentifyHeatwaveDays {
    # Test Case 1
    $result1 = Identify-HeatwaveDays -Temperatures @(32.5, 33.6, 35.1, 36.8, 37.2, 36.5, 34.3, 33.0, 35.2, 36.5, 37.1) -Threshold 35.0 -ConsecutiveDays 3
    $expected1 = @(2, 3, 4, 5, 8, 9, 10)
    if (-not (Compare-Arrays $result1 $expected1)) {
        throw "Test 1 failed: Expected $($expected1 -join ', '), but got $($result1 -join ', ')"
    }

    # Test Case 2
    $result2 = Identify-HeatwaveDays -Temperatures @(30.0, 31.0, 32.0, 33.0, 34.0, 35.1, 36.0, 37.0) -Threshold 35.0 -ConsecutiveDays 2
    $expected2 = @(5, 6, 7)
    if (-not (Compare-Arrays $result2 $expected2)) {
        throw "Test 2 failed: Expected $($expected2 -join ', '), but got $($result2 -join ', ')"
    }

    # Test Case 3
    $result3 = Identify-HeatwaveDays -Temperatures @(34.9, 35.0, 35.1, 35.2, 35.3, 35.4) -Threshold 35.0 -ConsecutiveDays 4
    $expected3 = @(2, 3, 4, 5)
    if (-not (Compare-Arrays $result3 $expected3)) {
        throw "Test 3 failed: Expected $($expected3 -join ', '), but got $($result3 -join ', ')"
    }

    Write-Output "All tests passed successfully"
}

Test-IdentifyHeatwaveDays

