# Get-MinParenthesesToAdd calculates the minimum number of parentheses required to make each sequence valid.
# A sequence is valid if all parentheses are properly closed in the correct order.
# The function iterates through each character in the sequence, tracking the number of unmatched opening and closing parentheses.
function Get-MinParenthesesToAdd {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string[]]$Sequences
    )

    $results = @()

    foreach ($seq in $Sequences) {
        $openNeeded = 0
        $closeNeeded = 0

        foreach ($char in $seq.ToCharArray()) {
            if ($char -eq '(') {
                $openNeeded++
            }
            elseif ($char -eq ')') {
                if ($openNeeded -gt 0) {
                    $openNeeded--
                }
                else {
                    $closeNeeded++
                }
            }
        }

        $results += $openNeeded + $closeNeeded
    }

    return $results
}

assertions:
function Test-GetMinParenthesesToAdd {
    $testCases = @(
        @{Sequence = "())"; Expected = 1},
        @{Sequence = "((("; Expected = 3},
        @{Sequence = "(()())"; Expected = 0},
        @{Sequence = "))(("; Expected = 4},
        @{Sequence = ""; Expected = 0}
    )

    foreach ($testCase in $testCases) {
        $result = Get-MinParenthesesToAdd -Sequences $testCase.Sequence
        if ($result -ne $testCase.Expected) {
            throw "Test failed for input '$($testCase.Sequence)'. Expected $($testCase.Expected) but got $result"
        }
    }

    Write-Output "All tests passed"
}

Test-GetMinParenthesesToAdd

