function Check-Anagram {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$FirstString,

        [Parameter(Mandatory = $true)]
        [string]$SecondString
    )

    if ($FirstString.Length -ne $SecondString.Length) {
        return "DIFERENTES"
    }

    # Initialize count arrays for each string
    $countFirst = [int[]]::new(256)
    $countSecond = [int[]]::new(256)

    # Count character frequencies in the first string
    foreach ($char in $FirstString.ToCharArray()) {
        $countFirst[[int]$char]++
    }

    # Count character frequencies in the second string
    foreach ($char in $SecondString.ToCharArray()) {
        $countSecond[[int]$char]++
    }

    # Compare the count arrays
    for ($i = 0; $i -lt 256; $i++) {
        if ($countFirst[$i] -ne $countSecond[$i]) {
            return "DIFERENTES"
        }
    }

    return "ANAGRAMAS"
}

assertions:
function Test-Anagram {
    # Test case 1: Valid anagram
    $result1 = Check-Anagram -FirstString "listen" -SecondString "silent"
    if ($result1 -ne "ANAGRAMAS") {
        throw "Test 1 failed: Expected 'ANAGRAMAS' but got '$result1'"
    }

    # Test case 2: Non-anagram
    $result2 = Check-Anagram -FirstString "hello" -SecondString "world"
    if ($result2 -ne "DIFERENTES") {
        throw "Test 2 failed: Expected 'DIFERENTES' but got '$result2'"
    }

    # Test case 3: Valid anagram with rearranged letters
    $result3 = Check-Anagram -FirstString "anagram" -SecondString "nagaram"
    if ($result3 -ne "ANAGRAMAS") {
        throw "Test 3 failed: Expected 'ANAGRAMAS' but got '$result3'"
    }

    # Test case 4: Different lengths
