function Count-SupersetSum {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$N,

        [Parameter(Mandatory = $true)]
        [int]$M,

        [Parameter(Mandatory = $true)]
        [int]$K,

        [Parameter(Mandatory = $true)]
        [int[]]$A
    )

    $MOD = 1000000007
    $maskMax = [Math]::Pow(2, $N)
    $cnt = New-Object int[] $maskMax

    # Initialize count for each mask
    foreach ($a in $A) {
        $cnt[$a]++
    }

    # High-dimensional prefix sum for superset summation
    for ($i = 0; $i -lt $N; $i++) {
        for ($mask = 0; $mask -lt $maskMax; $mask++) {
            if (-not ($mask -band (1 -shl $i))) {
                $cnt[$mask] = ($cnt[$mask] + $cnt[$mask -bor (1 -shl $i)]) % $MOD
            }
        }
    }

    $ans = 0
    for ($mask = 1; $mask -lt $maskMax; $mask++) {
        $bits = [Convert]::ToString($mask, 2).ToCharArray() | Where-Object { $_ -eq '1' } | Measure-Object | Select-Object -ExpandProperty Count
        $current = $cnt[$mask]
        $term = [Math]::Pow($current, $K) % $MOD
        if ($bits % 2 -eq 0) {
            $term = (-$term) % $MOD
        }
        $ans = ($ans + $term) % $MOD
    }

    return $ans % $MOD
}

