function Get-LargestNumber {
    param (
        [Parameter(Mandatory=$true)]
        [int[]]$Numbers
    )

    # Convert numbers to strings
    $strings = $Numbers | ForEach-Object { $_.ToString() }

    # Sort using custom comparison
    $sortedStrings = $strings | Sort-Object -Property @{Expression={$_}; Ascending=$false} -CustomComparer { Compare-Strings $args[0] $args[1] }

    # Join sorted strings and handle leading zeros
    $result = $sortedStrings -join ''
    $result = $result.TrimStart('0')

    if ([string]::IsNullOrEmpty($result)) {
        return '0'
    }

    return $result
}

