function Get-LongestArithmeticSeriesLength {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Array
    )

    if ($Array.Count -eq 0) {
        return 0
    }
    if ($Array.Count -eq 1) {
        return 1
    }

    # Sort the array to ensure elements are in ascending order
    $sortedArray = $Array | Sort-Object

    # Initialize a hashtable to store the dynamic programming state
    $dp = @{}
    $maxLength = 1

    for ($i = 0; $i -lt $sortedArray.Count; $i++) {
        for ($j = $i + 1; $j -lt $sortedArray.Count; $j++) {
            $diff = $sortedArray[$j] - $sortedArray[$i]
            $key = "$($sortedArray[$i]),$diff"
            $newKey = "$($sortedArray[$j]),$diff"

            if ($dp.ContainsKey($key)) {
                $dp[$newKey] = $dp[$key] + 1
            } else {
                $dp[$newKey] = 2
            }

            if ($dp[$newKey] -gt $maxLength) {
                $maxLength = $dp[$newKey]
            }
        }
    }

    return $maxLength
}

