# Find-ArrayIntersection calculates the intersection of two arrays, including duplicates.
# Each element in the result appears as many times as it shows in both arrays.
function Find-ArrayIntersection {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Array1,

        [Parameter(Mandatory = $true)]
        [int[]]$Array2
    )

    # Create hashtables to count frequencies of each element in both arrays
    $counts1 = @{}
    $counts2 = @{}

    # Count frequencies in Array1
    foreach ($num in $Array1) {
        if ($counts1.ContainsKey($num)) {
            $counts1[$num]++
        } else {
            $counts1[$num] = 1
        }
    }

    # Count frequencies in Array2
    foreach ($num in $Array2) {
        if ($counts2.ContainsKey($num)) {
            $counts2[$num]++
        } else {
            $counts2[$num] = 1
        }
    }

    $intersection = @()

    # Find the intersection by comparing counts
    foreach ($key in $counts1.Keys) {
        if ($counts2.ContainsKey($key)) {
            $minCount = [Math]::Min($counts1[$key], $counts2[$key])
            $intersection += ,$key * $minCount
        }
    }

    return $intersection
}

