function Partition {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [ref]$Arr,
        [Parameter(Mandatory = $true)]
        [int]$First,
        [Parameter(Mandatory = $true)]
        [int]$Last
    )

    $pivot = $Arr.Value[$Last]
    $i = $First - 1
    for ($j = $First; $j -lt $Last; $j++) {
        if ($Arr.Value[$j] -le $pivot) {
            $i++
            # Swap elements at positions i and j
            $temp = $Arr.Value[$i]
            $Arr.Value[$i] = $Arr.Value[$j]
            $Arr.Value[$j] = $temp
        }
    }
    # Swap the pivot element with the element at i+1
    $temp = $Arr.Value[$i+1]
    $Arr.Value[$i+1] = $Arr.Value[$Last]
    $Arr.Value[$Last] = $temp
    return $i+1
}

function QuickSort {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Array
    )

    function _QuickSortInternal {
        param (
            [ref]$Arr,
            [int]$First,
            [int]$Last
        )

        if ($First -lt $Last) {
            $pivotIndex = Partition -Arr $Arr -First $First -Last $Last
            _QuickSortInternal -Arr $Arr -First $First -Last ($pivotIndex - 1)
            _QuickSortInternal -Arr $Arr -First ($pivotIndex + 1) -Last $Last
        }
    }

    # Create a reference to the array to allow in-place modifications
    $arrayRef = [ref]$Array
    _QuickSortInternal -Arr $arrayRef -First 0 -Last ($Array.Count - 1)
    return $Array
}

