function Get-LargestNumber {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    # Convert numbers to strings for easier manipulation
    $numsStr = $Numbers | ForEach-Object { $_.ToString() }

    # Sort with a custom comparator (sort by concatenated string in descending order)
    $numsStr = $numsStr | Sort-Object -Property { $_ * 10 } -Descending

    # Form the largest number by concatenating the sorted strings
    $largestNum = $numsStr -join ''

    # Edge case: Handle leading '0's by stripping them except the last possible '0'
    if ($largestNum[0] -eq '0') {
        return '0'
    }

    return $largestNum
}

