function Get-MinimalTimeDifference {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Tasks
    )

    $sumTotal = ($Tasks | Measure-Object -Sum).Sum
    $maxPossible = [Math]::Floor($sumTotal / 2)

    # Initialize dynamic programming array
    $dp = New-Object bool[] ($maxPossible + 1)
    $dp[0] = $true

    foreach ($t in $Tasks) {
        # Update the dp array in reverse to avoid reusing the same element
        for ($j = $maxPossible; $j -ge $t; $j--) {
            if ($dp[$j - $t]) {
                $dp[$j] = $true
            }
        }
    }

    # Find the largest j <= maxPossible that is achievable
    $ans = $sumTotal
    for ($j = $maxPossible; $j -ge 0; $j--) {
        if ($dp[$j]) {
            $ans = $sumTotal - $j
            break
        }
    }

    return $ans
}

