# Process-NumbersAndGenerateLoops processes an array of integers, sorts them, and generates loops for consecutive ranges.
# The function identifies consecutive integer ranges and formats them into loop statements.
function Process-NumbersAndGenerateLoops {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Numbers
    )

    if ($Numbers.Count -eq 0) {
        return
    }

    # Sort the numbers in ascending order
    $sortedNumbers = $Numbers | Sort-Object

    $loops = @()
    $currentA = $sortedNumbers[0]
    $currentB = $sortedNumbers[0]

    # Iterate through the sorted numbers to find consecutive ranges
    for ($i = 1; $i -lt $sortedNumbers.Count; $i++) {
        $num = $sortedNumbers[$i]
        if ($num -eq ($currentB + 1)) {
            $currentB = $num
        } else {
            $loops += ,($currentA, $currentB)
            $currentA = $currentB = $num
        }
    }
    $loops += ,($currentA, $currentB)

    return $loops
}

# Format-LoopStatements takes an array of loop ranges and formats them into loop statements.
function Format-LoopStatements {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [System.Tuple[int, int][]]$Loops
    )

    $formattedStatements = @()
    foreach ($loop in $Loops) {
        $a, $b = $loop
        $formattedStatements += "for (int i = $a; i <= $b; i++) cout << i << `" `"; "
    }

    return $formattedStatements
}

