# Split-TextIntoSentences splits the input text into sentences based on punctuation marks (., ?, !) and returns the first N sentences.
function Split-TextIntoSentences {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$Text,

        [Parameter(Mandatory = $true)]
        [int]$SentenceCount
    )

    $punctuations = @('.', '?', '!')
    $positions = @()

    # Find all punctuation positions in the text
    for ($i = 0; $i -lt $Text.Length; $i++) {
        if ($Text[$i] -in $punctuations) {
            $positions += $i
        }
    }

    $sentences = @()
    $start = 0

    # Extract the first N sentences
    foreach ($pos in $positions[0..($SentenceCount - 1)]) {
        $end = $pos + 1
        $sentences += $Text.Substring($start, $end - $start)
        $start = $end
    }

    return $sentences
}

# Count-NamesInSentence counts the number of valid names in a given sentence.
# A valid name starts with an uppercase letter followed by lowercase letters.
function Count-NamesInSentence {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [string]$Sentence
    )

    $punctuations = @('.', '?', '!')
    $words = $Sentence -split '\s+'
    $count = 0

    foreach ($word in $words) {
        # Check if the word ends with a punctuation
        if ($word[-1] -in $punctuations) {
            $base = $word.Substring(0, $word.Length - 1)
        } else {
            $base = $word
        }

        # Skip empty bases
        if ([string]::IsNullOrEmpty($base)) {
            continue
        }

        # Check if base contains only letters
        if (-not ($base -match '^[a-zA-Z]+$')) {
            continue
        }

        # Check if first character is uppercase and the rest are lowercase
        if ($base[0] -ne $base[0].ToString().ToUpper()[0]) {
            continue
        }

        $valid = $true
        for ($i = 1; $i -lt $base.Length; $i++) {
            if ($base[$i] -ne $base[$i].ToString().ToLower()[0]) {
                $valid = $false
                break
            }
        }

        if ($valid) {
            $count++
        }
    }

    return $count
}

