function Rearrange-ArrayElements {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int[]]$Array
    )

    $n = $Array.Count
    $a_sorted = $Array | Sort-Object
    $res = New-Object int[] $n
    $left = 0
    $right = $n - 1

    for ($i = 0; $i -lt $n; $i++) {
        if ($i % 2 -eq 0) {  # odd positions (1-based)
            $res[$i] = $a_sorted[$left]
            $left++
        }
        else {  # even positions (1-based)
            $res[$i] = $a_sorted[$right]
            $right--
        }
    }

    # Check if the result meets the conditions
    $possible = $true
    for ($i = 1; $i -lt $n; $i++) {
        if ($i % 2 -eq 1) {  # even index (1-based), should be >= previous
            if ($res[$i] -lt $res[$i-1]) {
                $possible = $false
                break
            }
        }
        else {  # odd index >1 (1-based), should be <= previous
            if ($res[$i] -gt $res[$i-1]) {
                $possible = $false
                break
            }
        }
    }

    if ($possible) {
        return $res -join ' '
    }
    else {
        return "Impossible"
    }
}

