# Function to calculate the value of the cubic equation: f(x) = 6x + 2x - x
function Calculate-CubicValue {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$x
    )
    return 6 * [Math]::Pow($x, 3) + 2 * [Math]::Pow($x, 2) - $x
}

# Function to find the integer x where f(x) equals the target value using binary search
function Find-CubicSolution {
    [CmdletBinding()]
    param (
        [Parameter(Mandatory = $true)]
        [int]$Target
    )

    # Find the upper bound for binary search
    $high = 1
    while ((Calculate-CubicValue -x $high) -lt $Target) {
        $high *= 2
    }

    $left = $high / 2
    $right = $high
    $ans = 0

    # Binary search to find the solution
    while ($left -le $right) {
        $mid = [Math]::Floor(($left + $right) / 2)
        $val = Calculate-CubicValue -x $mid

        if ($val -eq $Target) {
            $ans = $mid
            break
        }
        elseif ($val -lt $Target) {
            $left = $mid + 1
        }
        else {
            $right = $mid - 1
        }
    }

    return $ans
}

